package lesson1;

public class MyClass {
	/* 
	 byte (number, 1 byte )
	 short (number, 2 byte)
	 int (number, 4 byte)
	 long (number, 8 byte)
	 float (float number, 4 byte)
	 double (float number, 8 byte)
	 char (a character, 2 byte)
	 boolean (true or false, 1 byte)
	 */
		
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int my_variable = 10;
		float my_decimal = (float)4.5;
		double my_double = 11.56;
		char my_char = 'A';
		boolean is_true = true;
		System.out.println("first package");
		System.out.println(my_variable);
		System.out.println(my_decimal);
		System.out.println(my_double);
		System.out.println(my_char);
		System.out.println(is_true);
		System.out.println("My variable value is "+ my_variable);
		
		
	}

}
