package lesson2;
/*
 IF Else statements and relational operators
 */
public class statements {
	/*
	 if{boolean expression}
	 {
	 	execute if the boolean expresion is true
	 }
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 10;
		
		if (x == 10) {
			System.out.println("yes x is equal to 10");
		}
		else {
			System.out.println("yes x is not equal to 10");
		}
	}

}
